mapper.select:
SELECT USERID, USERPASS 
  FROM MEMBER
 WHERE USERID = #{userId}
   AND USERPASS = #{userPass}

mapper.update:
UPDATE MEMBER SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.delete:
DELETE FROM member
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}

mapper.select:
SELECT COUNT(*) FROM member
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
